import React from 'react'
import propTypes from 'prop-types'
import { View, Vibration } from 'react-native';
import { CardViewWithImage } from 'react-native-simple-card-view'

const s = 1000;

export const RandomCard = ({ navigation, route}) =>{
    let list = route.params.list;
    let card = getRandomCard(list);
    
    return(
        <View>
            <CardViewWithImage
            width={300}
            source={{uri: card.image.small}}
            content={ 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. At aut distinctio!' }
            title={ card.name }
            imageWidth={ '100%' }
            imageHeight={ 500 }
            roundedImage={ false }
          />
        </View>
    )
}

const getRandomCard = (list) =>{
    let card = list[Math.floor(Math.random()*list.length)];
    return card;
  }

RandomCard.prototype ={
    card: propTypes.func
}
